INSERT INTO `file` (`id`, `date`, `name`, `disc`, `doc`, `pathServ`, `pathWeb`, `type`, `create_date`, `create_user`) VALUES (8,'1922-02-23 00:00:00','sorvasheva.jpg','кандидат филологических наук, специалист в области диалектологии и лексикографии коми языка','','/var/www/html/vm/img/sorvasheva-211.jpg','/vm/img/sorvasheva-211.jpg',0,'2024-03-29 08:53:02',2),(9,'1922-02-23 00:00:00','zhilina.jpg','кандидат филологических наук, специалист в области диалектологии и лексикографии коми языка','','/var/www/html/vm/img/zhilina-342.jpg','/vm/img/zhilina-342.jpg',0,'2024-03-29 09:06:40',2),(10,'1928-03-04 00:00:00','qulyaev.jpg','кандидат филологических наук, старший научный сотрудник','','/var/www/html/vm/img/gulyaev-300.jpg','/vm/img/gulyaev-300.jpg',0,'2024-03-29 09:38:06',2),(11,'1911-08-15 00:00:00','selkov.jpg','Сельков Николай Никитич (1911–1992), специалист в области лексикографии, синтаксиса, диалектологии коми языка.','','/var/www/html/vm/img/selkov-432.jpg','/vm/img/selkov-432.jpg',0,'2024-03-29 09:49:41',2),(12,'1934-09-30 00:00:00','loskutova.jpg','Лоскутова Нина Ильинична (1934–2023), специалист в области диалектологии и лексикографии коми языка.','','/var/www/html/vm/img/loskutova-751.jpg','/vm/img/loskutova-751.jpg',0,'2024-03-29 09:58:15',2),(13,'1933-07-29 00:00:00','pavlova.jpg','Павлова Эльба Кирилловна (1933–2013), старший лаборант сектора языка.','','/var/www/html/vm/img/pavlova-875.jpg','/vm/img/pavlova-875.jpg',0,'2024-03-29 10:23:25',2),(14,'1944-03-27 00:00:00','Костромина И.Н.','','','/var/www/html/vm/img/kostromina-443.jpg','/vm/img/kostromina-443.jpg',0,'2024-04-01 08:54:15',2),(15,'1994-03-08 00:00:00','Бараксанов Г.Г.','','','/var/www/html/vm/img/baraksanov-162.jpg','/vm/img/baraksanov-162.jpg',0,'2024-04-02 06:59:26',2),(19,'1980-04-25 00:00:00','Лыткин Василий Ильич','','','/var/www/html/vm/img/lytkin-719.jpg','/vm/img/lytkin-719.jpg',0,'2024-04-03 13:46:57',2),(20,'1980-08-13 00:00:00','Туркин Адольф Иванович','','','/var/www/html/vm/img/Turkin-602.jpg','/vm/img/Turkin-602.jpg',0,'2024-04-03 13:54:43',2),(21,'0000-00-00 00:00:00','Манова Надежда Дмитриевна','','','/var/www/html/vm/img/manova-689.jpg','/vm/img/manova-689.jpg',0,'2024-04-03 14:33:08',2),(23,'1981-01-01 00:00:00','Итконен Эркки','','','/var/www/html/vm/img/Erkki_itkonen_1981-776.jpg','/vm/img/Erkki_itkonen_1981-776.jpg',0,'2024-04-04 08:05:06',2),(24,'2000-03-27 00:00:00','Буров Григорий Михойлович','','','/var/www/html/vm/img/БуровГ-912.jpg','/vm/img/БуровГ-912.jpg',0,'2024-04-04 08:18:13',2),(25,'1984-03-27 00:00:00','Колегова Нина Андреевна','','','/var/www/html/vm/img/Kolegova-272.jpg','/vm/img/Kolegova-272.jpg',0,'2024-04-05 07:11:37',2),(26,'2010-04-27 00:00:00','Безносикова Люция Михайловна','','','/var/www/html/vm/img/Beznosikova-447.jpg','/vm/img/Beznosikova-447.jpg',0,'2024-04-05 07:30:10',2),(27,'1991-04-02 00:00:00','Ляшев Владимир Александрович','','','/var/www/html/vm/img/Lyashev-564.jpg','/vm/img/Lyashev-564.jpg',0,'2024-04-05 07:39:30',2),(28,'2004-04-13 00:00:00','Ракин Анатолий Николаевич','','','/var/www/html/vm/img/Rakin-794.jpg','/vm/img/Rakin-794.jpg',0,'2024-04-05 07:51:06',2),(29,'2000-03-27 00:00:00','Безносикова Люция Михайловна','','','/var/www/html/vm/img/Безносикова1-298.jpg','/vm/img/Безносикова1-298.jpg',0,'2024-04-05 07:54:34',2),(30,'2001-03-05 00:00:00','Безносикова Люция Михайловна','','','/var/www/html/vm/img/Безносикова2-654.jpg','/vm/img/Безносикова2-654.jpg',0,'2024-04-05 07:55:41',2),(31,'1985-07-24 00:00:00','Шестой Международный конгресс финно-угороведов','Участники конгресса А.Н. Ракин и Джон Гордон Коутс','','/var/www/html/vm/img/24-651.jpg','/vm/img/24-651.jpg',0,'2024-04-08 06:34:34',2),(32,'1965-06-25 00:00:00','Регистрация участников конференции по финно-угроведению','Павлова Э.К., Лоскутова Н.И., Муравьева Г.А. на регистрация участников конференции по финно-угроведению, июнь 1965 г.',' ','/var/www/html/vm/img/9-660.jpg','/vm/img/9-660.jpg',0,'2024-04-08 08:09:12',2),(33,'1970-04-15 00:00:00','Проводы Колеговой Н.А. на заслуженный отдых, 1970 г.','В 1970 г. вышли на пенсию старейшие коми языковеды М.А. Сахарова и Н.А. Колегова,  работавшие в отделе языка и литературы Коми филиала АН СССР, на базе которого и были созданы отделы языка и литературы и фольклора Института ЯЛИ.','','/var/www/html/vm/img/5-142.jpg','/vm/img/5-142.jpg',0,'2024-04-08 08:19:04',2);
