INSERT INTO `event` (`id`, `Name`, `DateN`, `DateK`, `Desc_short`, `Desc`, `Doc`, `importance`, `latitude`, `longitude`, `create_user`, `create_date`) VALUES (2,'Создание отдела языка Института языка, литературы и истории. ','1970','','На момент формирования Института языка, литературы и истории отдел языка состоял из 8 человек: кандидаты филологических наук с.н.с. В.А. Сорвачева (1945–1977), Т.И. Жилина (1946 –1982), Е.С. Гуляев (1954–1977), м.н.с. Н.Н. Сельков (1945–1976) и ст. лаборанты Н.И. Лоскутова (1958–1991), Э.К. Павлова ','На момент создания отдел языка состоял из 8 человек: кандидаты филологических наук с.н.с. В.А. Сорвачева (1945–1977), Т.И. Жилина (1946 –1982), Е.С. Гуляев (1954–1977), м.н.с. Н.Н. Сельков (1945–1976) и ст. лаборанты Н.И. Лоскутова (1958–1991), Э.К. Павлова (1961–1989), И.Н. Костромина (1967–1986), Р.И. Коснырева (род. в 1967). В связи с созданием института и нового отдела на должность заведующего был приглашен кандидат филологических наук Г.Г. Бараксанов, тогда научный сотрудник Института языкознания АН СССР. ','',2,'','',2,'2024-03-29 10:24:44'),(4,'Вышел в свет первый коми этимологический словарь, составленный В. И. Лыткиным и Е. С. Гуляевым.','1970','','«Краткий этимологический словарь коми языка» - это словарь, который даёт систематизированные сведения о происхождении коми слов, реконструируются древние формы слов и их значения. Авторы: В.И. Лыткин, Е.С. Гуляев. Работа внесла существенный вклад в развитие исторической лексикологии финно-угорских я','Словарь содержит около 2900 словарных статей и охватывает все буквы коми алфавита. Аналогичные словари создавались за рубежом, но к моменту издания Коми словаря эти проекты не были завершены.  Авторы Краткого этимологического словаря из числа 2900 этимологий приблизительно 2/3 систематизировали, интерпретировали по-своему, около одной трети всех этимологий являются совершенно новыми. Словарь дает систематизированные сведения о происхождении коми слов, определяет самобытный или заимствованный характер того или иного слова. Посредством сравнительно-исторического анализа реконструируется древний общекоми, или общепермский и допермский облик слова и его значение, прослеживаются языковые связи коми народа с соседними народами.\r\nВ 1971 г. В.И.Лыткин и Е.С.Гуляев получили диплом лауреатов Государственной премии Коми АССР им. А.И.Куратова за «Краткий этимологический словарь коми языка».\r\n','',2,'','',2,'2024-04-01 10:01:48'),(7,'Создан Коми научно-исследовательский институт','1934.04.17','','Образован Коми научно-исследовательский институт краеведения','В составе института было создано 2 отдела: отдел истории, культуры и быта и отдел языка, литературы и искусства. Его сотрудники изучали коми язык, литературу, историю, экономику, природные ресурсы региона. Основной задачей КНИИК являлась «координация и обобщение научно-исследовательской работы, проводимой разными областными и внеобластными учреждениями и организациями, организация исследований по истории, культуре и экономике Коми области в соответствии с задачами народнохозяйственного развития, а также составление первого тома Коми советской энциклопедии». Президиум АН СССР оказал помощь в комплектовании библиотеки института. Директором стал Д.И.Шулепов, ученым секретарем – А.И.Бабушкин (в августе 1934 г. «в связи с загруженностью его по основной работе» А.И.Бабушкин был освобожден от обязанностей ученого секретаря Коми НИИК, но продолжал участвовать в деятельности института). Институт вначале состоял из отделов национальной культуры, промышленности, сельского хозяйства. КНИИК подчинялся Управлению университетов и научно-исследовательских учреждений Наркомпроса РСФСР, а финансировался из местного бюджета. При создании КНИИК принимался во внимание разработанный А.С.Сидоровым проект, и сам он (как и ряд его коллег по ОИКК) сотрудничал в этом институте до своего отъезда в Ленинград. ','',1,'','',2,'2024-04-02 06:39:43'),(15,'Издано постановление Совнаркома Коми АССР №706 о передаче из системы Коми Наркомпроса НИИ языка, письменности и истории коми народа Базе АН СССР в Коми АССР','1944.09.19','',NULL,'','',2,'','',2,'2024-04-03 11:59:18'),(16,'Президент АН СССР В.Л.Комаров распорядился образовать комиссию для принятия Научно-исследовательского института языка, письменности и истории коми народа в состав Базы','1944.08.21','',NULL,'На основании распоряжения заместитель директора Базы И.И.Оплеснин 5 сентября 1944 г. издал приказ о создании комиссии и принятии до 15 сентября КНИИ в состав Базы «по состоянию на 1-е сентября с.г.» В тот же день И.И.Оплеснин издал приказ о зачислении в штат Базы сотрудников КНИИ: «В связи с приемкой Института языка, письменности в систему Базы АН и преобразование его в отдел Базы АН СССР в Коми АССР, зачислить в штаты Базы АН… следующих сотрудников: Подорову Агнию Ивановну – заведующую отделом; Сидорова Алексея Семеновича – старшего научного сотрудника; Мальцеву Нину Андреевну – и.о. старшего научного сотрудника; Сахарову Марфу Александровну − и.о. старшего научного сотрудника; Воробьеву Татьяну Павловну – уборщицу; Тимушева Дмитрия Андреевича – младшего научного сотрудника». В течение сентября процесс приемки НИИ в состав Базы был завершен.','',2,'','',2,'2024-04-03 12:18:41'),(17,'Становление Научно-исследовательского института отделом Базы ','1944.10.01','',NULL,'Наступил новый этап в развитии коми гуманитарной науки. Первое десятилетие работы было, в сущности, временем становления научных исследований по коми истории и филологии в академической Базе (в 1949 г. преобразованной в Коми филиал АН СССР). Главной трудностью был недостаток квалифицированных научных кадров. В октябре 1944 г. в отделе работало всего пять научных сотрудников: лингвисты Н.А.Мальцева (Колегова), А.И.Подорова, М.А.Сахарова, А.С.Сидоров, Д.А.Тимушев. Эта небольшая группа и стала основой для формирования будущего коллектива Института языка, литературы и истории. С 1944 г. отделом (сектором) заведовала А.И.Подорова, в 1947 г. ее сменил А.М.Мишарин. Научным лидером коллектива являлся А.С.Сидоров. В 1945 г. консультантом сектора стал Д.В.Бубрих; позднее методологическую помощь языковедам оказывал В.И.Лыткин [14, с. 29−30]. Именно лингвистика являлась основным научным направлением в деятельности сектора языка, письменности и истории Коми базы АН СССР. А.С.Сидоров, Н.А.Колегова, М.А.Сахарова, Д.А.Тимушев, А.И.Подорова занимались разработкой грамматики коми языка, изучением его диалектов, составлением коми-русского словаря, разработкой терминологии. В конце 1945 г. в секторе появился еще один языковед – В.А.Сорвачева, а в 1946 г. – трое: Т.И.Жилина, Н.Н.Сельков, Г.А.Нечаев (работавший еще в начале 30-х гг. с В.И.Лыткиным в Комиссии по изучению диалектов; правда, из сектора он ушел уже в 1948 г.). В.А.Сорвачева и Т.И.Фролова (Жилина), а также А.Н.Федорова и историк В.И.Цывунина в марте 1946 г. стали первыми аспирантами Базы АН СССР в Коми АССР. Так сложился коллектив коми лингвистов, плодотворно работавший не одно десятилетие.','',2,'','',2,'2024-04-03 12:22:21'),(18,'Издано постановление Совнаркома Коми АССР «О составлении капитального труда очерков по истории народа коми».','1945.08.01','',NULL,'Институт истории Академии наук СССР поддержал инициативу сотрудников отдела языка, письменности и истории Базы Академии наук СССР в Коми АССР провести совместную работу по составлению капитального труда по истории коми народа в объеме 25-30 печатных листов. Руководство этой работой возложено на члена-корреспондента Академии наук СССР Сергея Владимировича Бахрушина.','',2,'','',2,'2024-04-03 12:25:22'),(19,'Сектору языка, письменности и истории коми народа были поставлены задачи: разработка общественно-политической терминологии на коми языке, консультационная помощь местным организациям и газете «Вöрлэдзись» «с то','1945.03','',NULL,'В связи с быстрым развитием социалистической экономики и культуры Коми АССР и возросшей политической активностью широких масс населения коми язык обогатился общественно-политической терминологией. Исследование предусматривало составление русско-коми словаря общественно-политических терминов в качестве пособия-справочника для переводчиков, составителей учебной литературы, широких кругов советских, партийных и комсомольских работников, а также учителей.','',2,'','',2,'2024-04-03 12:27:14'),(20,'Отделом языка, письменности и истории коми народа начата работа по составлению «Русско-коми словаря общественно-политических терминов»','1945.3','',NULL,'Работа над словарем начата в марте 1945 г. К концу 1946 г. был собран материал из литературных источников: произведений Маркса и Энгельса, Ленина и Сталина, «Краткого курса истории ВКП(б)», Конституции СССР, РСФСР и Коми АССР, кодексов законов и др.; использованы словари: общественно-политический, философский и др., а также периодическая литература. На основе собранного материала составлен словник и начата работа по переводу терминов. Словарь содержит в себе около 3,5 тысяч терминов.','',3,'','',2,'2024-04-03 12:30:13'),(21,'Сектор языка, письменности и истории народа коми преобразован в сектор коми языка, письменности, литературы и истории','1947','',NULL,'В сектор были приняты в 1945 г. А.А.Вежев, который публиковал статьи по вопросам литературоведения и критику. В 1946 г. в аспирантуру поступила А.Н.Федорова, которая занялась поэзией И.А.Куратова, вопросами литературоведения - П.Г.Доронин.','',3,'','',2,'2024-04-03 12:32:34'),(22,'Состоялись ученые советы Базы, где рассматривались «космополитические извращения сталинского учения о нации в области изучения истории, языка и литературы народа коми».','1949','',NULL,'Под «извращением сталинского учения» подразумевался ревизия «нового учения о языке», или «яфетическая теория» акад. Н.Я.Марра. Основу теории составляли две идеи, касавшиеся исторического развития языка. Согласно Н.Я.Марру, языковое развитие идет от множества к единству. Языки возникали независимо друг от друга. Затем происходил процесс скрещивания: два языка в результате взаимодействия превращались в новый третий язык. По мнению Н.Я.Марра, языки развивались по единым законам. Все языки проходят одни и те же стадии, определяемые уровнем социально-экономического развития. При изменении экономического базиса язык как часть надстройки подвергается революционному взрыву и становится качественно иным. Н.Я.Марр считал, что «новое учение о языке» требует нового лингвистического мышления.\r\nОт лингвистов требовали полного признания «нового учения о языке». После сессии ВАСХНИЛ с лета 1948 г. в советском языкознании началась погромная кампания. На заседании Ученого совета Научно-исследовательской Базы АН СССР в Коми АССР 27 мая 1949 г. обсуждали работу сектора языка литературы и истории в связи с названными выше событиями. Заместитель директора Н.И.Шишкин утверждал, что буржуазная наука проводит идею о принадлежности севера России древним финским племенам (пранародам), что эту идею подхватили К.Ф.Жаков и П.А.Сорокин, позднее Д.В.Бубрих (основоположник финноугроведения), а вслед за ним сотрудники Базы А.С.Сидоров, Н.А.Колегова, А.И.Подорова. Ученый совет принял постановление, которое требовало кардинального пересмотра планов работы сектора языка, литературы и истории; программ и учебников коми языка, и приведению их в соответствие с «новым учением о языке».','',3,'','',2,'2024-04-03 12:36:00'),(23,'На заседании Президиума Коми филиала АН СССР обсудили развитие языкознания в Коми филиале в свете статей И.В. Сталина «Относительно марксизма в языкознании» и «К некоторым вопросам языкознания»','1950.07.28','',NULL,'Заведующий сектором языка, литературы и истории филиала А.М.Мишарин признал, что работа сектора была полностью перестроена, в соответствии с ошибочными, как теперь признано, решениями и указаниями Президиума Академии наук СССР по вопросам советского языкознания и марровского «нового учения» о языке.\r\nСектор языка и истории признал, что венцом заблуждений и ошибок была «марровская сессия», проведенная в феврале 1950 г., на которой была изложена новая идейная и методологическая позиция, стоившая сектору более полугодовой работы. \r\nРабота сектора на протяжении продолжительного периода времени подвергалась критике «как не могущие поспевать за ходом социалистического строительства, за темпами жизни». Основные работы сотрудников сектора квалифицировались как космополитические, идеалистические, бесполезные. За работниками сектора в лучшем случае признавалось усердие, но ставилось под сомнение содержание работы. \r\nВ результате перестройки работы отдела под марровскую теорию о языке, а затем развенчания этой теории и очередного изменения приоритетов на протяжении 8 лет работы целого коллектива научных сотрудников в свет вышли только коми-русский словарь, переиздание краткого орфографического словаря, а также участие сотрудников в подготовке учебников для низших ступеней школ. Неправильные методические установки приводили к излишне суровой оценке работ сотрудников сектора, несправедливому увольнению нескольких талантливых ученых.','',3,'','',2,'2024-04-03 12:41:06'),(24,'По решению Президиума АН СССР в Сыктывкаре состоялось Научное совещание по вопросам языкознания','1952.1.28','',NULL,'Состоялось Научное совещание по вопросам языкознания. Организаторами выступали Президиум АН СССР, Институт языкознания и Коми филиал. Совещание было призвано подвести некоторые итоги развития финно-угорского языкознания после «разоблачения» взглядов Н.Я.Марра. В совещании участвовали представители Мордовской, Ма-рийской, Удмуртской АССР, Коми-Пермяцкого автономного округа, лингвисты из Коми АССР – всего 375 чел. Заслушано 11 докладов, в том числе 7 – сотрудников филиала. Д.филол.н. А.С.Сидоров сделал два доклада: «Влияние русского языка на грамматический строй коми языка» и «Пути развития коми литературного языка». Тезисы докладов опубликованы в том же году в Москве.','',3,'','',2,'2024-04-03 12:42:54'),(25,'Сидоров А.С. приступил к формированию архива Коми филиала АН СССР.','1952.2','',NULL,'А.С.Сидоров сдал в архив Коми филиала документы, собранные им лично и документы сектора языка, литературы и истории, в том числе копии документов Департамента исполнительной полиции об обустройстве дороги от Пинеги до Мезени за 1826 г., формулярного списка о службе коми поэта И.А.Куратова в Семиреченской области; Среди документов, переданных А.С.Сидоровым, были материалы Коми научно-исследовательского института, сохраненные им (работы В.А.Молодцова 1930-х гг., словник Коми советской энциклопе-дии, рукописи рабочих экземпляров различных словарей, другие документальные материалы отдела языка и литературы). Кроме того, он передал в архив работу И.Вихмана «Краткий очерк научной поездки к зырянам в 1901-1902 гг.», опубликованный в финском журнале Угро-финского общества в 1903 г. Перевод с немецкого выполнен В.В.Вульфертом. Эта работа положила начало формированию архивных фондов и организации в структуре Коми филиала АН СССР Научного архива.','',2,'','',2,'2024-04-03 12:58:14'),(26,'Принято постановление бюро Коми обкома КПСС о деятельности сектора языка, письменности, литературы и истории Коми филиала АН СССР.','1953.4.28','',NULL,'Бюро областного комитета партии указало на следующее: недостаточная результативность (за годы работы выпущен только один терминологический словарь); слабая проработка вопросов развития коми литературного языка и истории; раздробленность тем и большие сроки их выполнения; отсутствие должной интенсивности и напряженности труда («сотрудники работают только днем, надо создать условия, чтобы они могли работать и вечерами»); отсутствие высококвалифицированного научного руководства.','',3,'','',2,'2024-04-03 13:05:28'),(27,'Опубликованы первые крупные работы коми-лингвистов А.И.Подоровой «Коми орфографический словарь»; М.А.Сахаровой «Русско-коми терминологический словарь».','1953','',NULL,'','',3,'','',2,'2024-04-03 13:40:34'),(28,'Сектор языка, письменности, литературы и истории преобразован в Отдел языка, литературы и истории','1953','',NULL,'Преобразование связанно с приведением к единообразию структур филиалов АН СССР. За прошедшие годы научные подразделения Коми филиала окрепли, усилились новыми кадрами, поэтому в новой утвержденной структуре все секторы были  преобразованы в отделы.','',2,'','',2,'2024-04-03 14:41:29'),(29,'Издан учебник для вузов «Современный коми язык».','1955','',NULL,'Авторский коллектив в составе проф. В.И.Лыткин (старший научный сотрудник Института языкознания АН СССР), кандидатов филологических наук Т.И.Жилиной, Е.Г.Жижевой, Н.А.Колеговой, А.И.Подоровой, М. А.Сахаровой и В.А. Сорвачевой, преподавателей Коми педагогического института кандидата филологических наук А.И.Кипрушевой и А.О.Третьяковой. подготовил первый учебник для вузов по коми языку. \r\nВ учебнике подробно излагаются факты современного коми литературного языка и дается им научное освещение. В тех случаях, когда это необходимо, для сравнения или разъяснения тех или иных языковых фактов привлекается материал коми-пермяцкого литературного языка.\r\nАвторы не ограничились только своей непосредственной задачей. Помимо описания фактов языка они сообщают также и необходимые сведения об общих понятиях языка (понятие о языковой единице, о фонеме, об органах речи, о частях речи, о слове вообще и т.д.), иллюстрируя их примерами, взятыми из коми языка. Ценна работа и тем, что в ней содержится ряд исторических справок. Особенно удачны разделы о фонетике, о составе современной коми лексики, о частицах, а также введение в морфологию.\r\nВ грамматике даны необходимые сведения о коми народе, о коми диалектах, об истории изучения коми языка, о развитии лексикографии, об основах транскрипции, об отношении литературного языка к диалектам т.д. Учитывая научную и практическую значимость этого учебника. Президиум АН СССР удостоил его академической премии.\r\n','',3,'','',2,'2024-04-03 14:43:32'),(30,'Налажена переписка и обмен литературой с Академией наук Венгрии и с финноугорским обществом г. Хельсинки.','1955','',NULL,'','',4,'','',2,'2024-04-03 14:44:39'),(31,'Опубликован первый том «Очерков истории Коми АССР»','1955','',NULL,'Монография подготовлена совместными усилиями ученых Москвы и Коми АССР. В обобщающем труде освещается история народа коми с древнейших времен до февральской революции 1917. Использование в названии слова «Очерки» обусловлено тем, что многие вопросы истории региона еще не были изучены. В связи с этим несомненным достоинством работы был исследовательский характер большинства глав «Очерков». Авторами широко использовались как изданные, так и неопубликованные материалы центральных и местных архивов, привлечен археологический и этнографический материал.','',3,'','',2,'2024-04-03 14:45:53'),(32,'Президиум АН СССР издал Постановление «Об организации в Коми филиале АН СССР Отдела истории и этнографии. ','1957.10.18','',NULL,'Отдел языка, литературы и истории был разделен на два отдела: отдел языка и литературы и отдел истории и этнографии.','',2,'','',2,'2024-04-04 07:50:32'),(33,'Коми филиал АН СССР посетил профессор Хельсинского университета Эркки Итконен для ознакомления с постановкой работ по изучению коми языка и его диалектов.','1957.12','',NULL,'Эркки Итконен при помощи ученых филиала собрал образцы речи различных диалектов коми языка, представители которых работали в филиале и обучались в сыктывкарском пединституте.','',4,'','',2,'2024-04-04 08:07:01'),(34,'Отдел истории, и этнографии переименован в Отдел истории, этнографии и археологии.','1958','',NULL,'Изменения связаны с поступлением на работу в 1957 г. Г.М. Бурова, первого профессионального археолога в Коми филиале АН СССР. В том же году Г.М.Буров провел разведочные работы в среднем и верхнем течении р. Вычегды. Обнаружил более десятка открытых археологических памятников различных эпох: мезолита, раннего металла и раннего железного века. В 1958 г. была открыта тема «Эпоха камня и раннего металла в бассейнах рек Вычегды и Печоры».','',3,'','',2,'2024-04-04 08:18:44'),(35,'Издана монография Л.П.Лашука «Очерк этнической истории Печорского края», первая публикация, посвященная этнической истории коми. ','1958','',NULL,'','',3,'','',2,'2024-04-04 08:21:15'),(36,'Отдел языка и литературы Коми филиала АН СССР посетил венгерский языковед Эрик Васоли с целью изучения коми языка и его диалектов.','1959','',NULL,'','',3,'','',2,'2024-04-04 08:32:03'),(37,'Сотрудники отдела языка М.А.Сахарова и Н.А.Колегова участвовали в I Международном конгрессе финно-угроведов в г. Будапешт (Венгрия)','1960.09.20','',NULL,'','',4,'','',2,'2024-04-04 08:42:38'),(38,'В Тартуском государственном университете (г. Тарту)  состоялась защита кандидатской диссертации Л.М. Безносиковой ','1977','',NULL,'Диссертация на соискание ученой степени кандидата филологических наук «Роль диалектной лексики в формировании словарного состава коми литературного языка» (научный руководитель – к.филол.н. Е.С.Гуляев). В исследовании впервые подвергнуты подробному освещению вопросы обогащения литературного коми языка лексическими диалектизмами, вопросы взаимодействия письменной речи и диалектов в художественной литературе послевоенного ','',4,'','',2,'2024-04-05 07:33:57'),(39,'В Тартуском государственном университете (г. Тарту)  прошла защита кандидатской диссертации В.А. Ляшева «Фонетико-морфологические особенности вымского диалекта коми языка».','1977','',NULL,'Научный руководитель – д.филол.н. В.И.Лыткин. Работа посвящена исследованию особенностей фонетики и морфологии вымского диалекта, его связи с соседними диалектами. ','',4,'','',2,'2024-04-05 07:45:09'),(40,'В Тартуском государственном университете (г. Тарту)  А.Н. Ракин защитил кандидатскую диссертацию «Лексика  флоры коми языка».','1977','',NULL,'Впервые в коми языкознании системно исследована ботаническая народная номенклатура коми языка. Научный руководитель – д. филол.н. В.И.Лыткин.','',4,'','',2,'2024-04-05 08:02:24'),(42,'Отдел истории, этнографии и археологии начал разработку темы «Вопросы истории религии и атеизма в Коми АССР».','1960','',NULL,'В соответствии с Постановлением Президиума АН СССР от 17 апреля 1959 г. и указаниями Коми обкома КПСС отдел истории, этнографии и археологии начал разработку темы «Вопросы истории религии и атеизма в Коми АССР».','',4,'','',2,'2024-04-05 08:08:10'),(43,'Б.И.Гуслицер и В. И. Канивец открыли палеолитическую стоянку человека в верховьях р. Печора, в гроте Медвежьей пещеры.','1960','',NULL,'С 1958 г. комплексная геолого-археологическая экспедиция Коми филиала АН СССР вела систематические поиски палеолитических стоянок в бассейне р. Печора. В 1960 г. Б.И.Гуслицер и В. И. Канивец открыли палеолитическую стоянку человека в верховьях р. Печора, в гроте Медвежьей пещеры. В 1962 г. инженер-геолог Е.М.Тимофеев обнаружил палеолитическую стоянку у д. Бызовой. Медвежья пещера и Бызовая являются самыми северными стоянками палеолитического человека в мире, их возраст предварительно определяли в пределах 2030 тыс. лет до нашей эры. Археологические находки сдвинули на север границы расселения палеолитического человека, они доказали, что освоение территории Коми края началось значительно раньше, чем предполагалось до сих пор. В 1966-1970 гг. Коми филиал АН СССР разрабатывал тему «Палеолит Севера европейской части СССР» (исполнители Б.И.Гуслицер и В.И.Канивец). В 1967 г. на средней Печоре открыта третья палеолитическая стоянка – Крутая гора, что позволило сделать вывод о заселении Северо-Востока Европейской части страны не позднее среднего палеолит.','',3,'','',2,'2024-04-05 08:10:12'),(44,'Отдел истории, этнографии и археологии разделен на отдел истории и отдел этнографии и археологии.','1961.12','',NULL,'','',3,'','',2,'2024-04-05 08:34:34'),(45,'Организован Ученый совет по гуманитарным наукам','1962.06.23','',NULL,'Решение Президиума Коми филиала АН СССР об организации Ученого совета по гуманитарным наукам было вызвано потребностью координировать научные исследования отделов языка и литературы, истории, а также этнографии и археологии. Создание совета говорило о наличии достаточной «критической массы» квалифицированных специалистов и проводимых научных исследований. Совет стал организационной предпосылкой к созданию института гуманитарного профиля.','',2,'','',2,'2024-04-05 08:36:17'),(46,'Опубликован второй том «Очерков по истории Коми АССР», посвященный советскому периоду.','1962','',NULL,'Впервые издан обобщающий труд, описывающий историю Коми края от Октябрьской революции и до 1960-х гг. Авторский коллектив использовал большой фактический материал из фондов центральных архивов, Вологды, Архангельска, Коми республиканского и областного партийного архивов, материалы периодической печати, статистические сборники и другие источники». Многие документы впервые были введены в научный оборот. ','',3,'','',2,'2024-04-05 08:38:16'),(47,'В издательстве \"Наука\" опубликована монография «Канинская пещера», автор В.И.Канивец.','1964','',NULL,'','',3,'','',2,'2024-04-05 08:39:51'),(48,'Коми филиал АН СССР подготовил и провел Вторую Всесоюзную конференцию по финно-угроведению. ','1965.06.25','',NULL,'Присутствовали представители всех научных центров страны, работавшие по вопросам финно-угроведения.','',3,'','',2,'2024-04-05 08:41:42'),(49,'В Сыктывкаре состоялись первые Куратовские чтения, организованные отделом языка и литературы Коми филиала АН СССР.','1965.07.01','',NULL,'Инициатором Куратовских чтений стал поэт и ученый В.И. Лыткин. Чтения повысили интерес общественности Коми республики к творческому наследию поэта. В 1973 г., 1976 г., 1979 г., 1986 г., 1989 г., 1990 г. вышли 6 томов «Куратовских чтений». В их основу положены дополненные и переработанные доклады и сообщения куратоведов, прозвучавшие на семнадцати Куратовских чтениях и содержащие новые важные обобщения в куратоведении. Активное участие в Куратовских чтениях в разные годы принимали В.И. Лыткин, А.Е. Ванеев, А.К. Микушев, В.И. Мартынов и др., а также П.П. Домокош из Венгрии, Дж. Коутс из Великобритании, Н.М. Бассель, А.И. Туркин из Эстонии и др.  ','',3,'','',2,'2024-04-05 08:46:03'),(50,'В.И.Лыткину присвоено почетное звание заслуженного деятеля науки и техники Коми АССР за заслуги в области коми языкознания и литературоведения.','1965.09.03','',NULL,'','',3,'','',2,'2024-04-05 08:50:13'),(51,'П.И.Чисталеву присвоено звание заслуженного деятеля искусств Коми АССР за заслуги в области советского музыкального искусства и активную деятельность по пропаганде коми народной музыки.','1965','',NULL,'','',4,'','',2,'2024-04-05 08:56:37'),(52,'В г. Сыктывкаре состоялось V  Уральское археологическое совещание.','1967.02.27','',NULL,'Проведение в Сыктывкаре V Уральского археологического совещания являлось проявлением признания заслуг археологов Коми филиала АН СССР, которые провели плодотворную работу по выявлению и изучению памятников древности на территории Коми АССР. За предыдущие 10 лет ими открыто и исследовано более 300 стоянок, поселений, торфяников и могильников, относившихся к различным древним эпохам, начиная с палеолита и кончая средневековьем. Исключительный научный интерес представляют палеолитические стоянки в Медвежьей пещере и у д. Бызовая, многослойные поселения и торфяники в районе Синдорского озера, Турбинское поселение у д. Галово, Веслянский, Кищикольский и Ленский могильники и многие другие археологические памятники. Выделены оригинальные археологические культуры.\r\nНа основе новых открытий установлена совершенно иная периодизация заселения территории Коми АССР, выявлены новые группы трудовой деятельности, материальной и духовной культуры древних племен, обитавших в ее пределах, установлены новые пути миграционных процессов местного населения, выяснены многие стороны различных проблем этногенеза коми.\r\nАрхеологами опубликовано более 40 статей, монографий и коллективных трудов. Изданы три выпуска «Материалов по археологии Европейского Северо-Востока». Вышли из печати такие монографии, как «Канинская пещера», «Пещеры Печорского Урала», «Вычегодский край (очерки древней истории)» и «Археологические памятники Вычегодской долины».\r\n','',3,'','',2,'2024-04-05 08:59:31'),(53,'Проведена совместная фольклорно-диалектологическая экспедиция сотрудников отдела языка и литературы и венгерского языковеда Эрика Ю.Васои в Печорском и Удорском районах. ','1967','',NULL,'В результате на венгерском радио был организован цикл передач о коми советской литературе.','',4,'','',2,'2024-04-05 09:01:07'),(54,'На базе гуманитарных подразделений Коми филиала АН СССР создан Институт языка, литературы и истории.','1970.04.01','',NULL,'Вопрос об организации Института языка, литературы и истории Коми филиала АН СССР был поставлен еще в 1951 г., когда заместитель директора Коми филиала АН СССР Н.И.Шишкин 16 августа обратился в ЦК ВКП(б) с ходатайством об организации Института биологии, Института языка, литературы и истории, Института леса. Вопрос о создании института в Коми филиала поднимал регулярно. К середине 1960-х гг. сложилась развитая структура гуманитарных отделов, В качестве основных направлений научных исследований были определены изучение закономерностей развития коми языка, исследование его диалектов и генетических связей с родственными финно-угорскими языками; фольклора и коми литературы; вопросов происхождений северных финно-угорских народов; особенностей и закономерностей развития культуры и быта коми народа и коми-пермяков.','',1,'','',2,'2024-04-05 09:07:58'),(56,'Создана Научная библиотека','1944.07.01','',NULL,'История научной библиотеки ФИЦ Коми НЦ УрО РАН неразрывно связана с историей становления и развития академического учреждения в Республике Коми.\r\n1 июля 1944 г. была закончена реорганизация и утверждена структура  Базы Академии наук СССР в Коми АССР с научной библиотекой в ее составе.','НА ФИЦ Коми НЦ УрО РАН. Ф.1. Оп.18. Д.12.Л.11-14',1,'','',10,'2024-04-16 09:21:47'),(57,'Создан ИНСТИТУТ ХИМИИ Коми научного центра Уральского отделения РАН (Институт химии Коми НЦ УрО РАН РАН)','1995.12.19','',NULL,'ИНСТИТУТ ХИМИИ Коми научного центра Уральского отделения РАН (Институт химии Коми НЦ УрО РАН РАН) создан в 1995 г. на базе Отдела химии Коми филиала АН СССР, основанного в 1958 г. Становление и развитие института связано с деятельностью академика  РАН А.В. Кучина. Основные направления научной деятельности института: фундаментальные проблемы реакционной способности химических соединений, механизмы химических реакций, методология органического и неорганического синтеза; научные основы экологически безопасного и ресурсосберегающего использования растительного сырья и его компонентов для получения химических продуктов и материалов; физико-химические основы технологии получения керамических, композиционных и нано - материалов с использованием синтетического и природного (минерального и растительного) сырья, создание новых веществ и материалов на основе полимеров растительного происхождения; фундаментальные проблемы получения физиологически активных соединений на основе синтетических, полусинтетических и природных веществ; асимметрический синтез.','',1,'','',8,'2024-04-18 05:08:07');
